import time

class Wallets():
    def Wallet(self, products):
        print(len(products), products)
        for x in range(len(products)):
            print(products[x]['quote_currency'], len(products))
            if (products[x]['quote_currency'] == 'USD') or (products[x]['quote_currency'] == 'GBP'):
                print('pop')
                products.pop(x)
            time.sleep(1)
        print(len(products), products)
    def Wallet1(self, products):
        print(len(products), products)
        for product in products:
            print(product['quote_currency'], len(products))
            if (product['quote_currency'] == 'USD') or (product['quote_currency'] == 'GBP'):
                print('pop')
                products.pop()
            time.sleep(1)
        print(len(products), products)
        return products
    def Wallet2(self, products):
        print(len(products), products)
        for product in products:
            print(product['quote_currency'], len(products))
            if (product['quote_currency'] == 'USD') or (product['quote_currency'] == 'GBP'):
                print('pop')
                del(product)
            time.sleep(1)
        print(len(products), products)

